import React, {
    useState,
    useEffect
} from 'react'
import {
    Link
} from 'react-router-dom'
import {
    Button
} from './Button';
import './Navbar.css'
const Navbar = () => {
        const [click, setClick] = useState(false);
        const [button, setButton] = useState(true);

        const handleClick = () => {
            setClick(!click)
        }

        const closeMobileMenu = () => {

            setClick(false)
        }

        const showButton = () => {
            if (window.innerWidth <= 960) {
                setButton(false);
            } else {
                setButton(true);
            }
        }

        useEffect(
            () => {
                showButton()
            },
            []
        )
        window.addEventListener('resize', showButton)
        return ( <
            >
            <
            nav className = "navbar" >
            <
            Link to = "/"
            className = "navbar-logo"
            onClick = {
                closeMobileMenu
            } >
            SAM < i class = "fab fa-typo3" > < /i> <
            /Link>

            <
            div className = "menu-icon" >
            <
            i onClick = {
                handleClick
            }
            className = {
                click ? 'fas fa-times' : 'fas fa-bars'
            } > < /i> <
            /div> <
            ul className = {
                click ? 'nav-menu active' : 'nav-menu'
            } >
            <
            li className = "nav-item" >
            <
            Link to = "/"
            className = "nav-links"
            onClick = {
                closeMobileMenu
            } >
            Home <
            /Link> <
            /li> <
            li className = "nav-item" >
            <
            Link to = "/services"
            className = "nav-links"
            onClick = {
                closeMobileMenu
            } >
            Services <
            /Link> <
            /li> <
            li className = "nav-item" >
            <
            Link to = "/product"
            className = "nav-links"
            onClick = {
                closeMobileMenu
            } >
            Products <
            /Link> <
            /li>

            <
            li className = "nav-item" >
            <
            Link to = "/sign-up"
            className = "nav-links-mobile"
            onClick = {
                closeMobileMenu
            } >
            Sign - Up <
            /Link> <
            /li> <
            /ul> {
                button && < Button buttonStyle = 'btn--outline  ' > SIGN UP < /Button>} <
                    /nav> <
                    />
            )
        }

        export default Navbar
